con:config.script:
import de.interactive_instruments.xtf.*;
SOAPUI_I.init(log, context, testRunner);
import de.interactive_instruments.xtf.wfs.*;
ProjectHelper ph = new ProjectHelper();

Capabilities.loadInstance().firstFeatureType.transferProperties();

import de.interactive_instruments.xtf.*;
SOAPUI_I.init(log, context, testRunner);
import de.interactive_instruments.xtf.wfs.*;
ProjectHelper ph = new ProjectHelper();

def getFeatureResponse = ph.getTestStepResult("GetFeature");
def ft = Capabilities.loadInstance().firstFeatureType
for( ns in ft.namespaceHolder.entrySet) {
	getFeatureResponse.declareNamespace(ns.value, ns.key);
}

String firstTextualProperty

for(p in ft.getPropertiesByType(PropertyType.LITERAL)) {
	if ( !p.name.contains('gml')) {
		String val = getFeatureResponse.getNodeValue( '//' + ft.prefixAndName + '/' + p.name );
		if(val!=null && !val!="") {
			firstTextualProperty = p.name
			break;
		}
	}
}

// fallback 1
if( firstTextualProperty == null ) {
	for(p in ft.properties) {
		String val = getFeatureResponse.getNodeValue( '//' + ft.prefixAndName + '/' + p.name );
		if(val!=null) {
			firstTextualProperty = p.name
			break;
		}
	}
}

// fallback 2
if( firstTextualProperty == null ) {
	log.info("First textual property not found");
	String firstTextualPropertyNamespaces = getFeatureResponse.getNodeValue("namespace-uri((//*[namespace-uri(.)!='http://www.opengis.net/gml/3.2' and namespace-uri(.)!='urn:x-inspire:specification:gmlas:BaseTypes:3.2' and string-length(.)>0 and not(./*)])[1])")
	String firstTextualPropertyPrefix = Capabilities.loadInstance().firstFeatureType.namespaceHolder.getPrefixForNamespaceUri(firstTextualPropertyNamespace);
	firstTextualProperty = firstTextualPropertyPrefix + ':' + getFeatureResponse.getNodeValue("local-name((//*[namespace-uri(.)!='http://www.opengis.net/gml/3.2' and namespace-uri(.)!='urn:x-inspire:specification:gmlas:BaseTypes:3.2' and string-length(.)>0 and not(./*)])[1])")
}

ph.testCase.testSuite.setPropertyValue("FirstTextualProperty", firstTextualProperty)

	public static String xpathWithWildcardNamespaces(String xpath) {
		final String newPath='*:'
		int i = xpath.indexOf(':')+1
		if(i!=-1) {
			int nextPos = xpath.indexOf('/', i+1)
			for(; i!=-1 && nextPos!=-1;) {
				println i + " " + nextPos;
				println xpath.substring(i, nextPos)
				newPath += xpath.substring(i, nextPos) + '/*:';
				i = xpath.indexOf(':', i)+1;
				nextPos = xpath.indexOf('/', nextPos+1);
			}
			if(nextPos==-1 && i!=-1) {
				newPath += xpath.substring(i)
			}
			return newPath;
		}else{
			return xpath;
		}
	}

ph.testCase.testSuite.setPropertyValue("FirstTextualProperty", firstTextualProperty)
ph.testCase.testSuite.setPropertyValue("FirstTextualPropertyWithWildcards", xpathWithWildcardNamespaces(firstTextualProperty))

